package com.zhocm.common;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.PropertySource;/** * Created by zhaochengming on 2019/2/24. */@Configuration@ConfigurationProperties(prefix = "com.zhaocm")@PropertySource(value = "classpath:demo.properties")public class DemoResource {    private String name;    private String address;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }}