package com.zhocm.controller;import com.zhocm.User;import com.zhocm.common.DemoResource;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * Created by zhaochengming on 2019/2/23. */@RestController@RequestMapping("/user")public class DemoController {    @Autowired    private DemoResource demoResource;    @RequestMapping("/getUser")    public User getUser() {        User user=new User();        user.setName("1231221");        return user;    }    @RequestMapping("/getDemo")    public DemoResource getDemo() {        DemoResource bean = new DemoResource();        BeanUtils.copyProperties(demoResource,bean);        return bean;    }}